% primary component countcomp_count = 40; [tvec tlab tstv tstl] = readSets(); % let's check labels in both sets[unique(tlab)'; unique(tstl)']% compute and perform PCA transformation[mu trmx] = prepTransform(tvec, comp_count);tvec = pcaTransform(tvec, mu, trmx);tstv = pcaTransform(tstv, mu, trmx);% let's shift labels by one to use labels directly as indicestlab += 1;tstl += 1;shouldExtendFeatures = 0; % change 0 to 1 to enable expandFeatures% Extended features sets - Uncomment this section to use expandedFeaturesif (shouldExtendFeatures == 1)  extended_tvec = expandFeatures(tvec);  extended_tstv = expandFeatures(tstv);  tvec = extended_tvec;  tstv = extended_tstv;  endif;%OVOovo = trainOVOensamble(tvec, tlab, @perceptron);% train set[clab, classifiers_clab] = unamvoting(tvec, ovo);ovo_train_cfmx = confMx(tlab, clab)compErrors(ovo_train_cfmx)for i = 1:columns(classifiers_clab)    classifiers_clab{1, i}    ovo_train_cls_cfmx_full = confMx(tlab , classifiers_clab{2, i});    ovo_train_cls_cfmx = ovo_train_cls_cfmx_full(classifiers_clab{1,i},classifiers_clab{1,i})    compErrors(ovo_train_cls_cfmx)(1)endfor% test set[clab, classifiers_clab] = unamvoting(tstv, ovo);ovo_test_cfmx = confMx(tstl, clab)compErrors(ovo_test_cfmx)for i = 1:columns(classifiers_clab)    classifiers_clab{1, i}    ovo_test_cls_cfmx_full = confMx(tstl , classifiers_clab{2, i});    ovo_test_cls_cfmx = ovo_test_cls_cfmx_full(classifiers_clab{1,i},classifiers_clab{1,i})    compErrors(ovo_test_cls_cfmx)(1)endfor% OVRovr = trainOVRensamble(tvec, tlab, @perceptron);% train set[clab, classifiers_clab] = unamvoting_ovr(tvec, ovr);ovr_train_cfmx = confMx(tlab, clab)compErrors(ovr_train_cfmx)for i = 1:columns(classifiers_clab)    classifiers_clab{1, i}    org_lab = tlab;    org_lab(tlab == classifiers_clab{1, i}(1)) = 2;    org_lab(tlab != classifiers_clab{1, i}(1)) = 1;      clsf_lab = classifiers_clab{2, i};    clsf_lab(classifiers_clab{2, i} == classifiers_clab{1, i}(1)) = 2;    clsf_lab(classifiers_clab{2, i} != classifiers_clab{1, i}(1)) = 1;    ovr_train_cls_cfmx = confMx(org_lab , clsf_lab)    ovr_train_cls_cfmx = ovr_train_cls_cfmx(:, 1:2)    compErrors(ovr_train_cls_cfmx)(1)endfor% test set[clab, classifiers_clab] = unamvoting_ovr(tstv, ovr);ovr_test_cfmx = confMx(tstl, clab)compErrors(ovr_test_cfmx)for i = 1:columns(classifiers_clab)    classifiers_clab{1, i}    org_lab = tstl;    org_lab(tstl == classifiers_clab{1, i}(1)) = 2;    org_lab(tstl != classifiers_clab{1, i}(1)) = 1;      clsf_lab = classifiers_clab{2, i};    clsf_lab(classifiers_clab{2, i} == classifiers_clab{1, i}(1)) = 2;    clsf_lab(classifiers_clab{2, i} != classifiers_clab{1, i}(1)) = 1;    ovr_test_cls_cfmx = confMx(org_lab , clsf_lab);    ovr_test_cls_cfmx = ovr_test_cls_cfmx(:, 1:2)    compErrors(ovr_test_cls_cfmx)(1)endfor