% implement actf & check it on a graphx = -5:0.1:5;plot(x, actf(x))% implement actdf % note that input value for actdf is not x itself but actf(x)plot(x, actdf(actf(x)))% implement backprop % it makes sense to start with a really small datasetload tiny.txttlab = tiny(:,1);tvec = tiny(:,2:end);layersWeights = crann([columns(tvec) [4, 4] 2]);clsRes = anncls(tvec, layersWeights);cfmx = confMx(tlab, clsRes);errcf = compErrors(cfmx)[layersWeights terr] = backprop(tvec, tlab, layersWeights, 0.5)clsRes = anncls(tvec, layersWeights);cfmx = confMx(tlab, clsRes);errcf = compErrors(cfmx)% after reaching 100% classification % you can (probably) play with ann_training